import React from 'react';
import styled from 'styled-components';
import '../App.css';
const Diveiei=styled.div`

margin:50px;
padding:20px;`
;
const Divnaja=styled.fieldset`
background-color: gray;

padding:30px;`
const Divla=styled.div
`margin:10px
display:flex`;
;
class SEARCH extends React.Component{
  constructor(props) {
    super(props);
    this.state = { 
      menu: ""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e)
  {
        const target=e.target;
        const value=target.value;
        //const elementname=target.name;
        this.setState({menu:value})

  }
  handleSubmit(e)
  {
      //alert('A form was submitted: ' + this.state);
      e.preventDefault();
      this.selectAll();
      document.querySelector("#output").innerHTML = "";


  }
  selectAll() {
    console.log(this.state.menu);
    //let APi_key = "";
    let rooturl = "https://api.spoonacular.com/food/menuItems/search?apiKey=72815fe717c742788db375c048622213&query=" + this.state.menu;
    fetch(rooturl,
        {
          method: "GET",
          headers: {
            "content-type": "application/json",
            "accept": "application/json"
          },
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            let eiei = ''
            document.querySelector("#output").innerHTML += "<br><h1>Product list</h1><ul>";
            data.menuItems.forEach(each => {
                eiei += `<li> ${each.title}:<br> <img src ="${each.image}"></li>`
            })

            document.getElementById('output').innerHTML += eiei + "</ul>";
        })
        .catch((err) => {
            console.log(err);
        });
}

  render() 
  {
    return (
    <>
    <div>
      <h1>Find more Product</h1>
      <div>
      <form onSubmit={this.handleSubmit}>
      <input name="menu" type=" textb" value={this.state.menu}
                onChange= {this.handleChange}/>
        <input type="submit" value="submit" />
      </form>
      </div>

    </div>
    <div id="output">

    </div>
    </>

      )

  }
}
export default SEARCH;
