import React from 'react';
import styled from 'styled-components';

const Diveiei=styled.div`
margin:50px;
padding:20px;`
;
const Form=styled.form
`
margin: auto;
background-color: gray;
height:400px;
width:300px;
border-radius:50px;
`;
const Labe=styled.label
`
 text-decoration:square;
`;
const Inp=styled.input
`
border-radius:10px;
width=:300px;
line-break:auto; 
`;
const Butt=styled.button
`
border-radius:10px
`;
const H1=styled.h1
`
text-align=center;
color:white;
`;
class LOGIN extends React.Component{
  constructor(props) {
    super(props);
    this.state = { 
      username: "",
      password:""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e)
  {
        const target=e.target;
        const value=target.value;
        const elementname=target.name;
        this.setState({[elementname]:value,})

  }
  handleSubmit(e)
  {
      //alert('A form was submitted: ' + this.state);
      e.preventDefault();
      this.selectAll();
      //document.querySelector("#output").innerHTML = "";
      
      
  }
  selectAll() {
    console.log(this.state.menu);
    //let APi_key = "";
    let rooturl = "http://localhost:3030/auth" + this.state.menu;
    fetch(rooturl,
        {
          method: "POST",
          headers: {
            "content-type": "application/json",
            "accept": "application/json"
          },
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            let eiei = ''
            document.querySelector("#output").innerHTML += "<br><h1>Product list</h1><ul>";
            data.menuItems.forEach(each => {
                eiei += `<li> ${each.title}:<br> <img src ="${each.image}"></li>`
            })

            document.getElementById('output').innerHTML += eiei + "</ul>";
        })
        .catch((err) => {
            console.log(err);
        });
}
  render() 
  {
    return (
        <>
        <div class="container">
      
        <div class="card">
            <form class="formnaja" onSubmit={this.handleSubmit} >
        
                <h1>Log in</h1>
                <input class="textb" type="text" placeholder="username" required id="username" name="username" value={this.state.username}
                onChange= {this.handleChange}/>
                <input class ="textb" type="password" placeholder="Password" id="password" name="password" pattern="[a-z,A-Z,0-9]{3,}" title="follow the formatletters @[a-zA-Z0-9]3 or more letters" required/>
                
                
                <input class="submitb" type="submit" value="Log in"/>            
            </form>
        </div>
      </div>
      </>
      
    )
   
  }
}

export default LOGIN;
// If you want to start measuring performance in your app, pass a function
