import React from 'react';
import styled from 'styled-components';
import '../App.css';
const Diveiei=styled.div`

margin:50px;
padding:20px;`
;
const Divnaja=styled.fieldset`
background-color: gray;

padding:30px;`
const Divla=styled.div
`margin:10px
display:flex`;
;
class SEARCH extends React.Component{
  constructor(props) {
    super(props);
    this.state = { 
      menu: ""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(e)
  {
        const target=e.target;
        const value=target.value;
        //const elementname=target.name;
        this.setState({menu:value})

  }
  handleSubmit(e)
  {
      //alert('A form was submitted: ' + this.state);
      e.preventDefault();
      this.selectAll();
      document.querySelector("#output").innerHTML = "";


  }
  selectAll() {
    console.log(this.state.menu);
    //let APi_key = "";
    let rooturl = "http://localhost:3030/search" + this.state.menu;
    fetch(rooturl,
        {
          method: "GET",
          headers: {
            "content-type": "application/json",
            "accept": "application/json"
          },
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            let out = ''
            document.querySelector("#output").innerHTML += "<br><h1>Product list</h1><ul>";
            data.menuItems.forEach(each => {
                out += `<li> ${each.Pname}:<br> <img src ="${each.pic}"></li>`
            })

            document.getElementById('output').innerHTML += out + "</ul>";
        })
        .catch((err) => {
            console.log(err);
        });
}

  render() 
  {
    return (
    <>
    <div class="container" id='top'style={{height:'500px;'}}>

    <form onSubmit={this.handleSubmit}>
          <h1>SHOPEME</h1>
          <input  class="sbox" type="text" id="q" name="keyword" placeholder="Seach...." required value={this.state.menu}
                onChange= {this.handleChange}/>
          <button class="submit" type="submit">Search</button>
      </form>
    </div>
    <div id="output">

    </div>
    <footer>        
    <div class="foo">
        <a href="#top"><h4>Back to Top</h4></a>

      </div>

      </footer>
    </>

      )

  }
}
