import React from 'react';
import styled from 'styled-components';

const Diveiei=styled.div`
margin:50px;
padding:20px;`
;
const Form=styled.form
`
margin: auto;
background-color: gray;
height:400px;
width:300px;
border-radius:50px;
`;
const Labe=styled.label
`
 text-decoration:square;
`;
const Inp=styled.input
`
border-radius:10px;
width=:300px;
line-break:auto; 
`;
const Butt=styled.button
`
border-radius:10px
`;
const H1=styled.h1
`
text-align=center;
color:white;
`;
class LOGIN extends React.Component{
  render() 
  {
    return (
        <>
        <div class="container">
      
        <div class="card">
            <form class="container" method="POST" action="/auth" >
        
                <h1>Log in</h1>
                <input class="textb" type="text" placeholder="username" required id="username" name="username"/>
                <input class ="textb" type="password" placeholder="Password" id="password" name="password" pattern="[a-z,A-Z,0-9]{3,}" title="follow the formatletters @[a-zA-Z0-9]3 or more letters" required/>
                
                
                <input class="submitb" type="submit" value="Log in"/>            
            </form>
        </div>
      </div>
      </>
    )
   
  }
}

export default LOGIN;
