import React from 'react';
import styled from 'styled-components';

import '../App.css';
const Divcon = styled.div
`
display: flex;
justify-content: center;
flex-direction: row;
flex-wrap: wrap;

`
const A=styled.a
`
text-decoration: none;
`
const MyForm= styled.form`background-color: #F2F3F4;text-align: left;padding: 20px;`;


class UMAN extends React.Component{
    constructor(props)
    {
        this.handleChange= this.handleChange.bind(this);
    }
    handleChange(e){
        const target=e.target;
        const value=target.value;
        const elementname=target.name;
        this.setState({[elementname]:value,});
    }
    render()
    {
        return(  <>
        <div>
            <MyForm>
                <label>Student ID: </label>
                <input name="UID" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="fname" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="lname" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="Email" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="address" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="pnumber" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                <input name="DOB" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                 <input name="username" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                 <input name="pword" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                 <input name="address" type="text" value={this.state.studentID} onChange={(e) => this.handleChange({ 
                    studentID: e.target.value})}/>
                    <button type="button">Insert</button> 
                    <button type="button">Update</button> 
                    <button type="button">Delete</button>
            </MyForm>
        </div>
        </>);
    }

      

  
}  
export default UMAN;


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
