import React from 'react';
import styled from 'styled-components';

import '../App.css';
const Divcon = styled.div
`
display: flex;
justify-content: center;
flex-direction: row;
flex-wrap: wrap;

`
const A=styled.a
`
text-decoration: none;
`
const MyForm= styled.form`background-color: #F2F3F4;text-align: left;padding: 20px;`;


class UMAN extends React.Component{
    constructor(props)
    {
        super(props);
        this.state={
            UID:"",
            fname:"",
            lname:"",
            Email:"",
            address:"",
            pnumber:"",
            DOB:"",
            username:"",
            pword:"",
            urole:"user",
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(e){
        const target=e.target;
        const value=target.value;
        const elementname=target.name;
        this.setState({[elementname]:value,});
    }
    render()
    {
        return(  <>
        <div>
            <MyForm>
                <label>Student ID: </label>
                <input name="UID" type="text" value={this.state.UID} onChange={(e) => this.handleChange({ 
                    UID: e.target.value})}/>
                <input name="fname" type="text" value={this.state.fname} onChange={(e) => this.handleChange({ 
                    fname: e.target.value})}/>
                <input name="lname" type="text" value={this.state.lname} onChange={(e) => this.handleChange({ 
                    lname: e.target.value})}/>
                <input name="Email" type="text" value={this.state.Email} onChange={(e) => this.handleChange({ 
                    email: e.target.value})}/>
                <input name="address" type="text" value={this.state.address} onChange={(e) => this.handleChange({ 
                    address: e.target.value})}/>
                <input name="pnumber" type="text" value={this.state.pnumber} onChange={(e) => this.handleChange({ 
                    pnumber: e.target.value})}/>
                <input name="DOB" type="date" value={this.state.DOB} onChange={(e) => this.handleChange({ 
                    DOB: e.target.value})}/>
                 <input name="username" type="text" value={this.state.username} onChange={(e) => this.handleChange({ 
                    username: e.target.value})}/>
                 <input name="pword" type="text" value={this.state.pword} onChange={(e) => this.handleChange({ 
                    pword: e.target.value})}/>
                 <select name="urole" type="text" value={this.state.urole} onChange={(e) => this.handleChange({ 
                    urole: e.target.value})}>
                        <option value="user">user</option>
                        <option value="admin">admin</option>
                    </select>
                    <button type="button">Insert</button>
                    <button type="button">search</button>  
                    <button type="button">Update</button> 
                    <button type="button">Delete</button>
            </MyForm>
        </div>
        </>);
    }

      

  
}  
export default UMAN;


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
