import React from 'react';
import styled from 'styled-components';

import '../App.css';
const Divcon = styled.div
    `
display: flex;
justify-content: center;
flex-direction: row;
flex-wrap: wrap;

`
const A = styled.a
    `
text-decoration: none;
`
const MyForm = styled.form`background-color: #F2F3F4;text-align: left;padding: 20px;`;


class UMAN extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            UID: "",
            fname: "",
            lname: "",
            Email: "",
            address: "",
            pnumber: "",
            DOB: "",
            username: "",
            pword: "",
            urole: "user",
        }
        this.handleChange = this.handleChange.bind(this);
        this.search = this.search.bind(this);
        this.insert = this.insert.bind(this);
        this.update = this.update.bind(this);
        this.delete = this.delete.bind(this);
    }
    insert() {
        /* Call WS to insert a new student */
        let user_data = {
            user: {
                UID: this.state.UID,
                fname: this.state.fname,
                lname: this.state.lname,
                Email: this.state.Email,
                address: this.state.address,
                pnumber: this.state.pnumber,
                DOB: this.state.DOB,
                username: this.state.username,
                pword: this.state.pword,
                urole: this.state.urole
            },
        };
        let rooturl = "http://localhost:3030/user";
        fetch(rooturl,
            {
                method: "POST",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify(user_data)
            })
            .then((response) => response.json())
            .then((data) => {
                alert("Insert successfully")

            })
            .catch((err) => {
                console.log(err);
            });

    }
    update() {
        let user_data = {
            user: {
                UID: this.state.UID,
                fname: this.state.fname,
                lname: this.state.lname,
                Email: this.state.Email,
                address: this.state.address,
                pnumber: this.state.pnumber,
                DOB: this.state.DOB,
                username: this.state.username,
                pword: this.state.pword,
                urole: this.state.urole
            },
        };
        let rooturl = "http://localhost:3030/user";
        fetch(rooturl,
            {
                method: "PUT",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify(user_data)
            })
            .then((response) => response.json())
            .then((data) => {
                alert("Update successfully")

            })
            .catch((err) => {
                console.log(err);
            });
    }
    delete() {/* Call WS to delete the student */

        let user_data = {
            UID: this.state.UID
        }
        let rooturl = "http://localhost:3030/user";
        fetch(rooturl,
            {
                method: "DELETE",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify(user_data)
            })
            .then((response) => response.json())
            .then((data) => {
                alert("user deleted");
            })
