import React from "react";
import '../App.css';






class DETAIL extends React.Component {

    componentDidMount() {
        this.detail_product()
    }

    detail_product() {


        var temp = window.location.search
        var key = temp.split("=");
        key = key[1];
        console.log(key)

        fetch("http://localhost:3030/allproductsdetail/" + key, {
            method: "GET",
            headers: {
                "content-type": "application/json",
                accept: "application/json",
            },
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data.data);
                let eiei = ''
                document.querySelector("#result").innerHTML += `<br><h1 style="color:black">Product list of "${this.state.menu}"</h1><ul>`;
                eiei += "<div class='container'>"
                  
                  eiei += "<div class='card' style='width: 18rem;'>";
                  eiei += "<img src='" + data.data.pic + "' class='card-img-top' alt='card'>"
                  eiei += "<div class='card-body'>"
                  eiei += "<h4><b>" + data.data.Pname + "</b></h4>";
                  eiei += "<section class='price'>$" + data.data.price + "</section><br>";
                  eiei += "<section class='des'>" + data.data.pinfo + "</section><br>";
                  eiei += "</div>"
                  eiei += "</div>"
                //output += "</div>"
               
    
                document.getElementById('result').innerHTML += eiei + "</div>";
            })
            .catch((err) => {
                console.log(err);
            });
    }



    render() {
        return (
            <>
                <section>
                    <h1>Product detail</h1>
                    <div id="result">
                        
                    </div>
                </section>
            </>
        );
    }
}

export default DETAIL;
