import React from 'react';
import styled from 'styled-components';

import '../App.css';
const Divcon = styled.div
    `
display: flex;
justify-content: center;
flex-direction: row;
flex-wrap: wrap;

`
const A = styled.a
    `
text-decoration: none;
`
const MyForm = styled.form`background-color: #F2F3F4;text-align: left;padding: 20px;`;


class PMAN extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            PID: "",
            pic: "",
            Pname: "",
            pinfo: "",
            price: "",
        }
       this.handleChange = this.handleChange.bind(this);
       this.search=this.search.bind(this);
       this.insert=this.insert.bind(this);
       this.update=this.update.bind(this);
       this.delete=this.delete.bind(this);
    }
    insert() {
        /* Call WS to insert a new student */
        let product_data = {
            product: {
                PID: this.state.PID,
                pic: this.state.pic,
                Pname: this.state.Pname,
                pinfo: this.state.pinfo,
                price: this.state.price,
            },
          };
        let rooturl = "http://localhost:3030/product";
        fetch(rooturl,
            {
                method: "POST",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify( product_data )
            })
            .then((response) => response.json())
            .then((data) => {
                alert("Insert successfully")
                
            })
            .catch((err) => {
                console.log(err);
            });

    }
    update() {
        let product_data = {
            product: {
                PID: this.state.PID,
                pic: this.state.pic,
                Pname: this.state.Pname,
                pinfo: this.state.pinfo,
                price: this.state.price,
            },
          };
        let rooturl = "http://localhost:3030/product";
        fetch(rooturl,
            {
                method: "PUT",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify( product_data )
            })
            .then((response) => response.json())
            .then((data) => {
                alert("Update successfully")
                
            })
            .catch((err) => {
                console.log(err);
            });
    }
    delete() {/* Call WS to delete the student */
        
        let product_data = {
              PID: this.state.PID
        }
        let rooturl = "http://localhost:3030/product";
        fetch(rooturl,
            {
                method: "DELETE",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
                body: JSON.stringify( product_data )
            })
            .then((response) => response.json())
            .then((data) => {
                alert("product deleted");
            })
            .catch((err) => {
                console.log(err);
            });
    }

    search()
    {
       
        let rooturl = "http://localhost:3030/product/"+this.state.PID;
        fetch(rooturl,
            {
                method: "GET",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
            })
            .then((response) => response.json())
            .then((data) => {
                console.log(data.data)
                let state=data.data;
                this.setState({state})
                console.log(this.setState)
                document.querySelector("#result").innerHTML="";
                let output = "";
                output += `<div  class="eiei"><h1 style="color:black"> user ID: ${this.state.UID} </h1>`;
                output += "<table class='table'>";
                output += "<thead>";
                output += "<tr>";
                output +=
                    "<th scope='col'>#</th><th scope='col'>First name</th><th scope='col'> Last name </th> <th scope='col'> Email </th> <th scope='col'> Address </th> <th scope='col'> Phone number </th>  <th scope='col'>DOB</th> <th scope='col'>Username</th> <th scope='col'> Password </th> <th scope='col'> Role </th>";
                output += "</tr>";
                output += "</thread>";
                output += "<tbody>";
