import React from 'react';
import styled from 'styled-components';

import '../App.css';
const Divcon = styled.div
    `
display: flex;
justify-content: center;
flex-direction: row;
flex-wrap: wrap;

`
const A = styled.a
    `
text-decoration: none;
`
const MyForm = styled.form`background-color: #F2F3F4;text-align: left;padding: 20px;`;


class UMAN extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            UID: "",
            fname: "",
            lname: "",
            Email: "",
            address: "",
            pnumber: "",
            DOB: "",
            username: "",
            pword: "",
            urole: "user",
        }
        this.handleChange = this.handleChange.bind(this);
    }
    insert() {
        /* Call WS to insert a new student */

        let rooturl = ""
        fetch(rooturl,
            {
                method: "GET",
                headers: {
                    "content-type": "application/json",
                    "accept": "application/json"
                },
            })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
            });
        //output += "</div>"

    }
    update() {
        /* Call WS to update the student */

    }
    delete() {/* Call WS to delete the student */

    }
    selectAll()
        {
            /* Call WS to get all students */
            let rooturl = "http://localhost:3030/users";
            fetch(rooturl,
                {
                    method: "GET",
                    headers: {
                        "content-type": "application/json",
                        "accept": "application/json"
                    },
                })
                .then((response) => response.json())
                .then((data) => {
                    console.log(data.Data);
                    let eiei = ''
                    document.querySelector("#output").innerHTML += `<br><h1> User list </h1><ul>`;
                    eiei += "<div class='container'>"
                    data.data.forEach(each => {
                        eiei += "<div class='card' style='width: 18rem;'>"
                        eiei += "<h1>UID:"+each.UID+"</h1>"
                        eiei += "<h4><b>" + each.fname + " " + each.lname + "</b></h4>";
                        eiei += "<section class='price'>$" + each.Email + "</section><br>";
                        eiei += "<section class='des'>" + each.pnumber + "</section><br>";
                        eiei += "</div>"
                        eiei += "</div>"
                    });
                    //output += "</div>"


                    document.getElementById('output').innerHTML += eiei + "</div>";
                })
                .catch((err) => {
                    console.log(err);
                });
        }
        handleChange(changeObject) {
            this.setState(changeObject);
        }

        render()
        {
            return (<>
                <a href="http://localhost:3000/admin"><button class="back">Back to home page</button></a>
                <h1 style={{ color: "black" }}>User management</h1>
                <div>
                    <MyForm onSubmit={this.handleSubmit}>
                        <label>UserID: </label>
                        <input name="UID" type="text" value={this.state.UID} onChange={this.handleChange} /><br />
                        <label>Fristname: </label>
                        <input name="fname" type="text" value={this.state.fname} onChange={this.handleChange} /><br />
                        <label>Lastname: </label>
                        <input name="lname" type="text" value={this.state.lname} onChange={this.handleChange} /><br />
                        <label>Email: </label>
                        <input name="Email" type="text" value={this.state.Email} onChange={this.handleChange} /><br />
                        <label>Address: </label>
                        <input name="address" type="text" value={this.state.address} onChange={this.handleChange} /><br />
                        <label>Phone number: </label>
                        <input name="pnumber" type="text" value={this.state.pnumber} onChange={this.handleChange} /><br />
                        <label>Date of birth: </label>
                        <input name="DOB" type="date" value={this.state.DOB} onChange={this.handleChange} /><br />
                        <label>Username: </label>
                        <input name="username" type="text" value={this.state.username} onChange={this.handleChange} /><br />
                        <label>Password: </label>
                        <input name="pword" type="text" value={this.state.pword} onChange={this.handleChange} /><br />
                        <label>Role: </label>
                        <select name="urole" type="text" value={this.state.urole} onChange={this.handleChange}><br />
                            <option value="user">user</option>
                            <option value="admin">admin</option>
                        </select><br />
                        <button type="button" onClick={this.insert}>Insert</button>
                        <button type="button" onClick={this.update}>Update</button>
                        <button type="button" onClick={this.delete}>Delete</button>
                        <button type="button" onClick={this.selectAll}>search all</button>
                    </MyForm>
                    <div id="output">
                    </div>
                </div>
            </>);
        }




    }
    export default UMAN;


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
